var workDays = [1, 2, 3, 4, 5]; // Man - Fre; Søn == 0, Lør == 6
var workStart = [8, 30]; // Start på arbejdsdagen som [T, M]
var workEnd = [17, 30]; // Slut på arbejdsdagen som [T, M]
if (args && args[0])
    workDays = args[0];
if (args && args[1])
    workStart = args[1];
if (args && args[2])
    workEnd = args[2];
if (prev)
    // Ikke forventet ved normal brug, men brugt som nuværende tidspunkt til test .
    next = new date(prev);
else
    next = new date();
// Hvis vi er efter sluttidspunktet af arbejdsdagen eller det ikke er en arbejdsdag, så gå videre til
// arbejdstids start den følgende dag.
while (next.getHours() > workEnd[0]) ||
       (next.getHours() == workEnd[0] && next.getMinutes () > workEnd[1]) ||
       (workdays.indexOf(next.getDay()) == -1)) {
    next.setDate(next.getDate() + 1);
    next.setHours(workStart[0]);
    next.setMinutes(workStart[1]);
}
// Hvis vi er før begyndelsen af arbejdsdagen, så gå til arbejdsdagens starttid.
if (next.getHours() < workStart[0]) ||
    (next.getHours() == workStart[0] && next.getMinutes() < workStart[1])) {
    next.setHours(workStart[0]);
    next.setMinutes(workStart[1]);
}

