// Lähetä ensimmäinen viesti nyt ja seuraavat viestit kerran päivässä.
if (! prev)
    next = new Date();
else {
    var now = new Date();
    next = new Date(prev); // Kopioi päivämääräargumentti, jotta emme muuta sitä.
    do {
        next.setDate(next.getDate() + 1);
    } while (next < now);
    // ^^^ Älä yritä lähettää menneisyydessä, mikäli Thunderbird oli
    // nukkumassa ajastettuna ajankohtana.
}
if (! args) // Lähetä viestit oletuksena kolme kertaa.
    args = [3];
nextargs = [args[0] - 1];
// Toista, jos emme ole vielä lähettäneet riittävän monta kertaa.
