// Enviar a primeira mensagem agora, mensagens posteriores uma vez ao dia.
if (! prev)
    next = new Date();
else {
    var now = new Date();
    next = new Date(prev); // Copiar o argumento da data tal que não seja modificado.
    do {
        next.setDate(next.getDate() + 1);
    } while (next < now);
    // ^^^ Não tentar enviar ao passado, caso o Thunderbird esteja adormecido no
    // horário agendado de envio.
}
if (! args) // Enviar mensagens três vezer por padrão.
    args = [3];
nextargs = [args[0] - 1];
// Repetir se ainda não tiver sido feito suficiente número de envios.
if (nextargs[0] > 0)
    nextspec = "function " + specname;
