// Skicka det första meddelandet nu, följande meddelanden en gång per dag.
if (! prev)
    next = new Date();
else {
    var now = new Date();
    next = new Date(prev); // Kopiera datum-argumentet så vi inte ändrar det.
    do {
        next.setDate(next.getDate() + 1);
    } while (next < now);
    // ^^^ Skicka inte i förfluten tid, ifall Thunderbird inte var aktivt vid den 
